// ==UserScript==
// @name         TwitchAdSolutions (strip-alt)
// @namespace    https://github.com/pixeltris/TwitchAdSolutions
// @version      1.0
// @description  Multiple solutions for blocking Twitch ads (strip-alt)
// @updateURL    https://github.com/pixeltris/TwitchAdSolutions/raw/master/strip-alt/strip-alt.user.js
// @downloadURL  https://github.com/pixeltris/TwitchAdSolutions/raw/master/strip-alt/strip-alt.user.js
// @author       pixeltris
// @match        *://*.twitch.tv/*
// @run-at       document-start
// @grant        none
// ==/UserScript==
(function() {
    'use strict';
    var twitchMainWorker = null;
    const oldWorker = window.Worker;
    window.Worker = class Worker extends oldWorker {
        constructor(twitchBlobUrl) {
            if (twitchMainWorker) {
                super(twitchBlobUrl);
                return;
            }
            var jsURL = getWasmWorkerUrl(twitchBlobUrl);
            if (typeof jsURL !== 'string') {
                super(twitchBlobUrl);
                return;
            }
            var newBlobStr = `
                ${processM3U8.toString()}
                ${hookWorkerFetch.toString()}
                AD_SIGNIFIER = 'stitched-ad';
                LIVE_SIGNIFIER = ',live';
                StreamInfos = [];
                StreamInfosByUrl = [];
                hookWorkerFetch();
                importScripts('${jsURL}');
            `
            super(URL.createObjectURL(new Blob([newBlobStr])));
            twitchMainWorker = this;
            var adDiv = null;
            this.onmessage = function(e) {
                if (e.data.key == 'UboShowAdBanner') {
                    if (adDiv == null) { adDiv = getAdDiv(); }
                    adDiv.P.textContent = 'Waiting for' + (e.data.isMidroll ? ' midroll' : '') + ' ads to finish...';
                    adDiv.style.display = 'block';
                }
                else if (e.data.key == 'UboHideAdBanner') {
                    if (adDiv == null) { adDiv = getAdDiv(); }
                    adDiv.style.display = 'none';
                }
            }
            function getAdDiv() {
                var playerRootDiv = document.querySelector('.video-player');
                var adDiv = null;
                if (playerRootDiv != null) {
                    adDiv = playerRootDiv.querySelector('.ubo-overlay');
                    if (adDiv == null) {
                        adDiv = document.createElement('div');
                        adDiv.className = 'ubo-overlay';
                        adDiv.innerHTML = '<div class="player-ad-notice" style="color: white; background-color: rgba(0, 0, 0, 0.8); position: absolute; top: 0px; left: 0px; padding: 10px;"><p></p></div>';
                        adDiv.style.display = 'none';
                        adDiv.P = adDiv.querySelector('p');
                        playerRootDiv.appendChild(adDiv);
                    }
                }
                return adDiv;
            }
        }
    }
    function getWasmWorkerUrl(twitchBlobUrl) {
        var req = new XMLHttpRequest();
        req.open('GET', twitchBlobUrl, false);
        req.send();
        return req.responseText.split("'")[1];
    }
    async function processM3U8(url, textStr, realFetch) {
        var haveAdTags = textStr.includes(AD_SIGNIFIER);
        if (haveAdTags) {
            var streamInfo = StreamInfosByUrl[url];
            if (streamInfo == null) {
                console.log('Unknown stream url ' + url);
                postMessage({key:'UboHideAdBanner'});
                return textStr;
            }
            streamInfo.IsMidroll = textStr.includes('MIDROLL');
            var lines = textStr.replace('\r', '').split('\n');
            for (var i = 0; i < lines.length; i++) {
                var line = lines[i];
                if (line.includes('stitched-ad')) {
                    // Someone should make this a regex...
                    var replaceTags = ['X-TV-TWITCH-AD-URL', 'X-TV-TWITCH-AD-CLICK-TRACKING-URL'];
                    for (var j = 0; j < replaceTags.length; j++) {
                        var adTag = replaceTags[j] + '="';
                        var adTagIndex = line.indexOf(adTag);
                        var adTagEndIndex = line.indexOf('"', adTagIndex + adTag.length);
                        line = line.substring(0, adTagIndex) + adTag + 'http://twitch.tv' + line.substring(adTagEndIndex);
                    }
                    lines[i] = line;
                }
                else if (line.startsWith('#EXTINF') && !line.includes(LIVE_SIGNIFIER) && lines.length > i + 1) {
                    streamInfo.AdUrlCache[lines[i + 1]] = 1;
                }
            }
            textStr = lines.join('\n');
        }
        return textStr;
    }
    function hookWorkerFetch() {
        var realFetch = fetch;
        fetch = async function(url, options) {
            if (typeof url === 'string') {
                if (url.endsWith('.ts')) {
                    var shownAdBanner = false;
                    for (const [channelName, streamInfo] of Object.entries(StreamInfos)) {
                        var seg = streamInfo.AdUrlCache[url];
                        if (seg) {
                            url = 'data:image/png;base64,';
                            postMessage({key:'UboShowAdBanner',isMidroll:streamInfo.IsMidroll});
                            shownAdBanner = true;
                            break;
                        }
                    }
                    if (!shownAdBanner) {
                        postMessage({key:'UboHideAdBanner'});
                    }
                }
                if (url.endsWith('m3u8')) {
                    return new Promise(function(resolve, reject) {
                        var processAfter = async function(response) {
                            var str = await processM3U8(url, await response.text(), realFetch);
                            resolve(new Response(str));
                        };
                        var send = function() {
                            return realFetch(url, options).then(function(response) {
                                processAfter(response);
                            })['catch'](function(err) {
                                console.log('fetch hook err ' + err);
                                reject(err);
                            });
                        };
                        send();
                    });
                }
                else if (url.includes('/api/channel/hls/') && !url.includes('picture-by-picture')) {
                    var channelName = (new URL(url)).pathname.match(/([^\/]+)(?=\.\w+$)/)[0];
                    return new Promise(async function(resolve, reject) {
                        var encodingsM3u8Response = await realFetch(url, options);
                        if (encodingsM3u8Response.status === 200) {
                            var encodingsM3u8 = await encodingsM3u8Response.text();
                            var streamInfo = StreamInfos[channelName];
                            if (streamInfo == null) {
                                StreamInfos[channelName] = streamInfo = {};
                            }
                            streamInfo.ChannelName = channelName;
                            streamInfo.Urls = [];
                            streamInfo.AdUrlCache = [];
                            streamInfo.IsMidroll = false;
                            var lines = encodingsM3u8.replace('\r', '').split('\n');
                            for (var i = 0; i < lines.length; i++) {
                                if (!lines[i].startsWith('#') && lines[i].includes('.m3u8')) {
                                    streamInfo.Urls.push(lines[i]);
                                    StreamInfosByUrl[lines[i]] = streamInfo;
                                }
                            }
                            resolve(new Response(encodingsM3u8));
                        } else {
                            resolve(encodingsM3u8Response);
                        }
                    });
                }
            }
            return realFetch.apply(this, arguments);
        }
    }
})();